service.mongodb.aggregate(
    at: <? $scope.options.db.url ?>,
    from: <? `${$scope.options.db.name}.examination` ?>,
    query: <? 
         [
          {
            '$match': {
              'patientId': $scope.options.id
            }
          }, {
            '$lookup': {
              'from': 'form', 
              'localField': 'id', 
              'foreignField': 'examinationId', 
              'as': 'forms'
            }
          }, {
            '$lookup': {
              'from': 'actor', 
              'localField': 'actorId', 
              'foreignField': 'id', 
              'as': 'physician'
            }
          }, {
            '$lookup': {
              'from': 'record', 
              'localField': 'id', 
              'foreignField': 'examinationId', 
              'as': 'records'
            }
          }, {
            '$project': {
              '_id': 0, 
              'type': 1, 
              'comment': 1, 
              'state': 1, 
              'dateTime': 1, 
              'patientId': 1, 
              'forms': 1, 
              'physician': 1, 
              'recordCount': {
                '$size': '$records'
              }
            }
          }, {
            '$project': {
              'records': 0
            }
          }
        ]
     ?> 
)

set("data")

<?javascript
    let data = $scope.data[0]
    if(data) {
        let formType = ["patient","echo","ekg"]
        let forms = formType.map( type => {
            let f = _.find(data.forms, d => d.type == type)
            if(f){
                let form  = f.data.en || f.data.uk
                if(form) return _.extend(form, { formType: type} )
            }
        }).filter( f => f)

        let physician
        if( data.physician ){
            physician = data.physician[0]
            physician = (physician) 
                ? {
                    name: `${physician.firstName} ${physician.lastName}`,
                    email: physician.email
                }
                : { name:"", email:"" }
        } else {
            physician = { name:"", email:"" }
        }


        $scope.result = {
            examination:{
                patientId: data.patientId,
                recordCount:data.recordCount,
                state: data.state,
                comment: data.comment,
                date: moment(new Date(data.dateTime)).format("YYYY-MM-DD HH:mm:ss"),
                physician
            },
            patient: _.find(forms, f => f.formType == "patient"),
            ekg: _.find(forms, f => f.formType == "ekg"),
            echo: _.find(forms, f => f.formType == "echo"),
        }
    } else {
        $scope.result = {}
    }    


?>

return("result")

// return("options")

/**
 **
 **     DATA PROCESSING SCRIPT: GET_GRANTS
 **     SERVICE: 
 **
 **/

service.mongodb.aggregate(
    at: <? $scope.options.db.url ?>,
    from: <? `${$scope.options.db.name}.${$scope.options.db.grantCollection}` ?>,
    query: <? 
        [   
            {
                $project:{ _id: 0 }
            }

        ] 
    ?> 
)

/**
 **
 **     DATA PROCESSING SCRIPT: UPDATE_RECORD
 **     SERVICE: 
 **
 **/


service.mongodb.insertMany(
    at: <? $scope.options.db.url ?>,
    in: <? `${$scope.options.db.name}.${$scope.options.db.historyCollection}` ?>,
    docs: <? ([_.extend({}, $scope.options.record)]) ?>
)

service.mongodb.bulk(
    at: <? $scope.options.db.url ?>,
    in: <? `${$scope.options.db.name}.${$scope.options.db.labelingCollection}` ?>,
    commands: <? 
        [
            {
                replaceOne:{
                    filter:{
                        id: $scope.options.record.id
                    },
                    replacement: _.extend({},$scope.options.record)
                }
            }
        ] 
    ?> 
)

