data.script:
let scripts = this.api.selectWidgets("gyhlq1nnspo").config.scripts
let getScript = name => _.find(scripts, s => s.name == name).script


let users = []
const getUserList =  () => {
    axios.get("./api/users/list")
        .then( response => {
            users = response.data        
        })
}
getUserList()

this.$localStorage.set("harvest1_config", {
    db: {
        url: "mongodb://localhost:27017",
        name: "sparrow",
        labelingCollection: "harvest1",
        metadataCollection: "harvest1-metadata",
        historyCollection: "harvest1-history"
    },
    recordId: window["Harvest 1 Labeling_query"].recordId
})

const userInfo = d => {
    if(!d) return {
        name: "N/A",
        photo: "",
        email: ""
    }    

    let u = _.find(users, u => [u.name, u.altname].includes(d))

    u = (u) 
        ? {
            name: d,
            photo: u.photo,
            email: u.email
        }    
        : {
            name: d,
            photo:"",
            email:""
        }
    return u    
}



this.on({
    event: "start",
    callback: async () => {

        console.log("!!!!QUERY",this.$localStorage.get("harvest1_config"))

        let response = await this.api.runDps(getScript("GET_RECORD"), {options: this.$localStorage.get("harvest1_config")})
        let record = response.data
        console.log("RECORD", response.data)

        let workflowParticipants = {
            "CMO": userInfo(response.data["CMO"]),
            "1st expert": userInfo(response.data["1st expert"]),
            "2nd expert": userInfo(response.data["2nd expert"])
        }    

        console.log(workflowParticipants)
        setTimeout(() => {
            selectWidgets("8xt7wl2pw9g").update({data: workflowParticipants}, {override: "options.data"})
        })

        response  = await this.api.runDps(getScript("GET_METADATA"), {options: this.$localStorage.get("harvest1_config")})
        let metadata = {} 
        console.log("METADATA", response.data)
        response.data.forEach( item => {
            metadata[item.name] = item
        })


        let instances = selectWidgets([

            "t7d9463tjo",
            "ub5oia8iq7b",

            "zb17pkfsfcm",
            "ub5oia8iq7b",
            "ivmrgnu39fl",
            "8rjugxx9ma",
            "nqtkk7jlv3o",
            "jakdrli4mv",
            "oyhzvsijtn",
            "vrv8claid18",
            "5b1xoz9cjxk",
            "ocortdrr2f",
            "d30ypt9v8p8",
            "044ud0wknm7d",
            "clsivx8hqpn",
            "dougod2v1s9",
            "fayak96njnh",
            "16v4e1dplfx",
            "hm7iuynb6gq",
            "iva3q60c6l",

            "qjtwpxdyu6",
            "39wymu04dw9",
            "i6kmcjqd41s",

            "oen7ydfnq7i",
            "vr35iazjm9",
            "58v0tpdzdaw",
            "3jebk80rpea",
            "6v74bcpv6cv"

        ])

        let namedInstances = [
            "Recording",
            "Patient",
            "Sound Presentation",
            "Recording Quality",
            "Class of the informativeness",
            "Type of Artifacts",
            "Loudness",
            "Split Type"


        ]

        // console.log(window.$data)

        setTimeout(() => {
            instances.forEach( d => {
                d.update({data: { record, metadata} },{override:"options.data"})
            })    
        }, 10)


        let tabsWidget = selectWidgets("exc82c574qg").getInstance()
        let navBarWidget = selectWidgets("yums7zgozwg").getInstance()
        let navButtonWidget = selectWidgets("9moznwe4427")

        navButtonWidget.hide()

        let view = {
            "Recording Info": selectSections(["sein9w9tfe", "21o1pbye80q"]),
            "Recording Validation": selectSections(["cge4tz9c0a", "o4h4ptvduyb","hqwya9um49g"]),
            "Heart Sounds":selectSections(["a5jbu31x6na", "ljw1qw0emh"]),
            "Heart Murmurs":selectSections(["g9j7lva1dqu", "cqhzf5yis9k"]),
            "Pathological Findings": [selectSections(["if5kj47z4o"])],
            "Lung Sound": selectSections(["pgoylkijd7d","5m1j629g1m3"]),
            "Data Summary": selectSections(["w8llxhwud9","e1dm5w7olvf"]),
            "Form Submission": selectSections(["334kc8dt4js", "ju35u6cditc"])
        }

        let workflow = [
            "Recording Info",
            "Recording Validation",
            "Heart Sounds",
            "Heart Murmurs",
            "Pathological Findings",
            "Lung Sound",
            "Sound Segmentation",
            "Form Submission"   
        ]

        let activeStage = 0


        const showView = name => {
            _.keys(view).forEach(key => {
                view[key].forEach( v => v.hide())    
            })
            view[name].forEach( v => v.show())
            activeStage = _.findIndex(workflow, d => d == name)
        }


        showView("Recording Info")

        this.on({
            event: "navigate",
            callback: data => {
                showView(data.title)
            }
        })

        this.on({
            event: "next",
            callback: () => {
                activeStage = (activeStage < workflow.length-1) ? activeStage+1 : activeStage
                tabsWidget.select(activeStage)
                this.emit("navigate", {title: workflow[activeStage]})
            }
        })

        this.on({
            event: "prev",
            callback: () => {
                activeStage = (activeStage > 0) ? activeStage-1 : activeStage
                tabsWidget.select(activeStage)
                this.emit("navigate", {title: workflow[activeStage]})
            }
        })

        this.on({
            event: "layout.h-bar",
            callback: data => {

                navBarWidget.options.rows[0].cols[0].decoration.icon = 
                    (navBarWidget.options.rows[0].cols[0].decoration.icon == "mdi-arrow-collapse-left")
                        ? "mdi-arrow-collapse-right"
                        : "mdi-arrow-collapse-left"
                if(navBarWidget.options.rows[0].cols[0].decoration.icon == "mdi-arrow-collapse-left"){
                    navButtonWidget.hide()    
                } else {
                    navButtonWidget.show()
                }

            }
        })

    }
})

this.on({
    event:"show-help",
    callback: (message, sender) => {
        let w = selectWidgets(sender.data.widget)
        console.log(w)
        if(w.hidden){
            w.show()
        } else {
            w.hide()
        }
    }
})

