const activateDialog  = (dialog, d ,m ) => dialog.showAndWait(d || {data:{}}, m || {override:"opts.data"})

const showWarning = message => {
    let dialog = selectWidgets("9cl6ol9jtda").getInstance()
    activateDialog(dialog, {data: {message}})
}


const getSelection = () => {
    let selected = selectWidgets("3qtce5ji2p6").getInstance().selected
    return selected.filter( s => _.find(window.app.currentData, r => r["Examination ID"] == s["Examination ID"]))
}

const removeFromSelection = items => {
    let selected = selectWidgets("3qtce5ji2p6").getInstance().selected
    selected = selected.filter( s => !_.find( items, t => t["Examination ID"] == s["Examination ID"]))
    selectWidgets("3qtce5ji2p6").getInstance().selected = selected
}


this.on({
    event: "open-forms",
    callback: async () => {
        let selected = getSelection()
        if(selected.length == 0){
            showWarning("No rows selected.")
        }
        if(selected.length > 5){
            showWarning("We open no more than 5 tabs at a time.")
        }
        let limit = (selected.length > 5) ? 5 : selected.length
        let showed = selected.splice(0,limit)
        
        removeFromSelection(showed)
         
        showed.forEach( f => {
            window.open(
            `${window.app.config.recordsUrl}${f["Examination ID"]}`,
            "_blank"
            )
        })
        
    }
})

this.on({
    event:"open-log",
    callback: () => {
        window.open(
            `./log-listener.html`,
            "_blank"
        )
    }
})

this.on({
    event:"sync-data",
    callback: async () => {
        
        await axios({
            method:"GET",
            url: "./api/data/sync"
        })
        
        window.open(
            `./log-listener.html`,
            "_blank"
        )
    }
})

