window.app.userInfo = d => {
    if(!d) {
        return {
            name: "N/A",
            photo: "",
            email: ""
        }
    }    

    let u = _.find(window.app.users, u => [u.name, u.namedAs].includes(d))

    u = (u) 
        ? {
            name: d,
            photo: u.photo,
            email: u.email
        }    
        : {
            name: d,
            photo:"",
            email:""
        }
    return u    
}

window.app.userShortAvatar = (d, multi) => {
    let u = (multi) ? {name: d} :  window.app.userInfo(d)
    return {
        component:   {
            "type": "avatar",
            "decoration": {
              "src": u.photo,
              "icon": (multi) ? "mdi-account-group-outline" : "mdi-account-circle-outline",
              "style": "padding:0 10px;",
              "classes": "flex",
              "size": 32,
            //   "title":  u.name,
              "subTitle": u.name
            }
        }
    }
}



