    const buildCustomQuery = data => {
        if(!data) return []

        let res = []

        if(data["Examination ID"]){
            res.push({
                $match:{
                    "Examination ID": {
                        $regex: data["Examination ID"]
                    }    
                }
            })
        }

        if(data["updated by"].length > 0){
            res.push({
                $match:{
                    "updated by": {
                        $in: data["updated by"]
                    }    
                }
            })
        }

        if(data["1st expert"].length > 0){
            res.push({
                $match:{
                    "1st expert": {
                        $in: data["1st expert"].map( d => ( d =="N/A" ) ? "" : d)
                    }    
                }
            })
        }
        if(data["2nd expert"].length > 0){
            res.push({
                $match:{
                    "2nd expert": {
                        $in: data["2nd expert"].map( d => ( d =="N/A" ) ? "" : d)
                    }    
                }
            })
        }
        return res
    }


    const buildSyncQuery = data => {

        // console.log("buildSyncQuery input", data)

        if(!data) return []

        let res = []

        if(data["Examination ID"]){
            res.push({
                $match:{
                    "Examination ID": {
                        $regex: data["Examination ID"]
                    }    
                }
            })
        }

        if(data["state"] && data["state"].length > 0){
            res.push({
                $match:{
                    "state": {
                        $in: data["state"]
                    }    
                }
            })
        }

        if(data["organization"] && data["organization"].length > 0){
            res.push({
                $match:{
                    "organization.name": {
                        $in: data["organization"]
                    }    
                }
            })
        }

        if(data["dateRange"] && data["dateRange"].length == 2){

            res.push({
                $match:{
                    $and:[
                        {$expr: {$gte: ["$updatedAt", { $toDate: new Date(data["dateRange"][0]).toISOString() } ] }},
                        {$expr: {$lte: ["$updatedAt", { $toDate: new Date(data["dateRange"][1]).toISOString() } ] }}
                    ]    
                }
            })
        }

        // console.log("buildSyncQuery >>>>", res)


        return res
    }


