    this.on({
        event: "change-dataset",
        callback: async (sender, data) => {
            // console.log("!!!!!!!!!!!!", JSON.stringify(data), window.app.currentDataset, window.localStorage.getItem("jace__currentDataset"))
            const d = window.localStorage.getItem("jace__currentDataset")
            if(d) {
                if(data && data != d){
                    window.localStorage.setItem("jace__currentDataset", data)
                    window.app.currentDataset = data
                    // console.log("Change", window.localStorage.getItem("jace__currentDataset"))

                    await bootstrap()
                }
            } else {
