/**
 **
 **     DATA PROCESSING SCRIPT: GET_TASKS
 **     SERVICE: 
 **
 **/
<?javascript
    $scope.options.pipeline = 
    [
      {
        '$group': {
          '_id': {
            'Examination ID': '$Examination ID', 
            'TODO': '$TODO'
          }, 
          'count': {
            '$count': {}
          }, 
          '1st expert': {
            '$addToSet': '$1st expert'
          }, 
          '2nd expert': {
            '$addToSet': '$2nd expert'
          }, 
          'CMO': {
            '$addToSet': '$CMO'
          }, 
          'lastUpdate': {
            '$push': {
              'updated at': '$updated at', 
              'updated by': '$updated by'
            }
          }
        }
      }, {
        '$project': {
          'Examination ID': '$_id.Examination ID', 
          'TODO': '$_id.TODO', 
          'count': 1, 
          '1st expert': 1, 
          '2nd expert': 1, 
          'CMO': 1, 
          'lastUpdate': 1
        }
      }, {
        '$group': {
          '_id': {
            'Examination ID': '$Examination ID'
          }, 
          'stat': {
            '$addToSet': {
              'TODO': '$TODO', 
              'count': '$count'
            }
          }, 
          '1st expert': {
            '$addToSet': '$1st expert'
          }, 
          '2nd expert': {
            '$addToSet': '$2nd expert'
          }, 
          'CMO': {
            '$addToSet': '$CMO'
          }, 
          'lastUpdate': {
            '$addToSet': '$lastUpdate'
          }
        }
      }, {
        '$project': {
          'Examination ID': '$_id.Examination ID', 
          'stat': 1, 
          '1st expert': {
            '$reduce': {
              'input': '$1st expert', 
              'initialValue': [], 
              'in': {
                '$setUnion': [
                  '$$value', '$$this'
                ]
              }
            }
          }, 
          '2nd expert': {
            '$reduce': {
              'input': '$2nd expert', 
              'initialValue': [], 
              'in': {
                '$setUnion': [
                  '$$value', '$$this'
                ]
              }
            }
          }, 
          'CMO': {
            '$reduce': {
              'input': '$CMO', 
              'initialValue': [], 
              'in': {
                '$setUnion': [
                  '$$value', '$$this'
                ]
              }
            }
          }, 
          '_id': 0, 
          'lastUpdate': {
            '$first': {
              '$sortArray': {
                'input': {
                  '$reduce': {
                    'input': '$lastUpdate', 
                    'initialValue': [], 
                    'in': {
                      '$setUnion': [
                        '$$value', '$$this'
                      ]
                    }
                  }
                }, 
                'sortBy': -1
              }
            }
          }
        }
      }, {
        '$project': {
          'Examination ID': 1, 
          'stat': 1, 
          '1st expert': 1, 
          '2nd expert': 1, 
          'CMO': 1, 
          '_id': 0, 
          'updated at': '$lastUpdate.updated at', 
          'updated by': '$lastUpdate.updated by'
        }
      }, {
        '$sort': {
        //   'updated at': -1,    
          'Examination ID': 1, 
        //   'updated by': 1, 

        }
      }
    ] 

    $scope.options.userFilter = ($scope.options.me)
        ? [
            {
                '$match': {
                  '$or': [
                    {
                      'updated by': $scope.options.me
                    }, {
                      '1st expert': $scope.options.me
                    }, {
                      '2nd expert': $scope.options.me
                    }, {
                      'CMO': $scope.options.me
                    }
                  ]
                }
            }    
        ]
        : []

    $scope.options.pageFilter = [
        {
            '$skip': $scope.options.eventData.skip
        }, {
            '$limit': $scope.options.eventData.limit
        }
    ]    


    $scope.options.countPipeline = [
        { $count: 'count'},
        { $project: {_id: 0} }
    ]

?>


