            if(!r["1st expert"] && !r["2nd expert"]) return "Assign 2nd expert"
            if(!r["1st expert"] && r["2nd expert"]) return "Assign 1st expert"
            if( r["1st expert"] ) return "Continue Labeling"


        } else {

            return r.TODO

        }
    }

    const resolveAssigment = r => {
        switch (r.TODO) {
            case "Assign 2nd expert"    : 
                return r["CMO"]
                break
            case "Assign 1st expert"    : 
                return r["2nd expert"]
                break
            case "Continue Labeling"    : 
                return r["1st expert"]
                break
            case "Complete 2nd Stage"   : 
                return r["2nd expert"]
                break
            case "Complete Labeling"    : 
                return r["CMO"]
                break
            case "Finalized"            : 
                return ""
                break
        }
    }

    $scope.records = $scope.records.map( r => {

        if( ["Assign 2nd expert", "Assign 1st expert"].includes(r.TODO) ){
            r = _.extend({}, r, $scope.assignator)
            r.TODO = resolveTodo(r)
            r["assigned to"] = resolveAssigment(r)
        } else {
            r["1st expert"] =  (r["1st expert"]) ? r["1st expert"] : $scope.assignator["1st expert"]
            r["2nd expert"] =  (r["2nd expert"]) ? r["2nd expert"] : $scope.assignator["2nd expert"]
            r["updated at"] = $scope.assignator["updated at"]
            r["updated by"] = $scope.assignator["updated by"]
            r.TODO = resolveTodo(r)
            r["assigned to"] = resolveAssigment(r)
        }   

        return r    


    })

    $scope.commands = $scope.records.map( r => ({
        replaceOne:{
            filter:{
                id: r.id
            },
            replacement: _.extend({},r)
        }
    }))
?>


service.mongodb.insertMany(
    at: <? $scope.options.db.url ?>,
    in: <? `${$scope.options.db.name}.${$scope.options.db.historyCollection}` ?>,
    docs: <? $scope.records ?>
)

