                         return app.record[key].length > 0
                     }
                     if( _.isString(app.record[key]) ){
                         return !!app.record[key]
                     }
                     return !_.isUndefined(app.record[key])

        })

        window.app.recordScore = {
            total: total.length,
            completed: completed.length,
            percent: Math.round(100* completed.length / total.length)
        }

    }


