            // console.log("METADATA", response.data)
            response.data.forEach( item => {
                metadata[item.name] = item
            })
            window.app.metadata = metadata
        }

        const loadForms = async patientId => {
            let response = await this.api.runDps(getScript("GET_FORMS"), {options: _.extend(window.app.config, {patientId})})
