                if(["dev", "admin", "CMO", "reader"].includes(user.role)) return

                if([window.app.record["1st expert"], window.app.record["2nd expert"]].includes(user.altname)) return

                user.role = "Reader"
        }





        const getTodoList = () => {

            let list = _.keys(window.app.state2Todo)

            if(["dev","admin", "CMO"].includes(user.role)) return list

            //   [
