        }


        let datasets = await loadDatasetMetadata()
        // console.log("datasets", datasets)
        window.app.availableDatasets = datasets.map( d => d.name)

        const DB_SETTINGS = _.find(datasets, d => d.name == window.app.currentDataset).settings

        this.$localStorage.set("harvest1_config", _.extend({}, DB_SETTINGS, {
            recordId: window[`${appName}_query`].recordId
        }))

        window.app.config = this.$localStorage.get("harvest1_config")
            // console.log ("START WITH CONFIG", window.app.config)

            // 
