    query: <? 
         [
          {
            '$match': {
              'patientId': $scope.options.id
            }
          }, {
            '$lookup': {
              'from': $scope.options.db.formCollection, 
              'localField': 'id', 
              'foreignField': 'examinationId', 
              'as': 'forms'
            }
          }, {
            '$lookup': {
              'from': $scope.options.db.userCollection, 
              'localField': 'actorId', 
              'foreignField': 'id', 
              'as': 'physician'
            }
          }, {
            '$lookup': {
              'from': $scope.options.db.labelingCollection, 
              'localField': 'id', 
              'foreignField': 'Examination ID', 
              'as': 'records'
            }
          }, {
            '$project': {
              '_id': 0, 
              'type': 1, 
              'comment': 1, 
              'state': 1, 
              'dateTime': 1, 
              'patientId': 1, 
              'forms': 1, 
              'physician': 1, 
              'recordCount': {
                '$size': '$records'
              }
            }
          }, {
            '$project': {
              'records': 0
            }
          }
        ]
     ?> 
)

set("data")

<?javascript
    let data = $scope.data[0]
    if(data) {
        let formType = ["patient","echo","ekg"]
        let forms = formType.map( type => {
            let f = _.find(data.forms, d => d.type == type)
            if(f && f.data){
                let form  = f.data.en || f.data.uk
                if(form) return _.extend(form, { formType: type} )
            }
        }).filter( f => f)

        let physician
        if( data.physician ){
            physician = data.physician[0]
            physician = (physician) 
                ? {
                    name: `${physician.firstName} ${physician.lastName}`,
                    email: physician.email
                }
                : { name:"", email:"" }
        } else {
            physician = { name:"", email:"" }
        }


        $scope.result = {
            examination:{
                patientId: data.patientId,
                recordCount:data.recordCount,
                state: data.state,
                comment: data.comment,
                date: moment(new Date(data.dateTime)).format("YYYY-MM-DD HH:mm:ss"),
                physician
            },
            patient: _.find(forms, f => f.formType == "patient"),
            ekg: _.find(forms, f => f.formType == "ekg"),
            echo: _.find(forms, f => f.formType == "echo"),
        }
    } else {
        $scope.result = {}
    }    


?>

