    if(!d) return {
        name: "N/A",
        photo: "",
        email: ""
    }    

    let u = _.find(users, u => [u.name, u.altname].includes(d))

    u = (u) 
        ? {
            name: d,
            photo: u.photo,
            email: u.email
        }    
        : {
            name: d,
            photo:"",
            email:""
        }
    return u    
}



