const showTableLoading = () => {
    setTimeout(() => {
        selectWidgets("3qtce5ji2p6").getInstance().options.decoration.loading = true
    }, 10)
}

const hideTableLoading = () => {
    setTimeout(() => {
        selectWidgets("3qtce5ji2p6").getInstance().options.decoration.loading = false
    }, 10)
}

const showInTable = data => {
    setTimeout(() => {
        data = _.extend(data, { selection: window.app.dataSelection} )
        selectWidgets("3qtce5ji2p6").update({ data }, { override: "options.data" })
    }, 10)
}

const updatePagination = data => {
    setTimeout(() => {
        selectWidgets("cssold5h515").update({ data }, { override: "options.data" })
    }, 10)
}

const buildTabTitle = (data, tab) => {
    let f = _.find(data, d => d.TODO == tab.$name)
    tab.title = tab.$name
    let newCount = (f) ? f.count : 0
    let diff = newCount - tab.count
    tab.count = newCount
    tab.diff = diff
    
    if ( diff > 0 ) {
        tab.badge = badge.warning(`+${diff}`)
    }
    
    if ( diff < 0 ) {
        tab.badge = badge.success(diff)
    }
    
    if ( diff == 0 ) {
        tab.badge = badge.primary(tab.count)
    }
    
    return tab
}

const badge = {
    primary: content => ({
        content,
        decoration:{
            class:"primary--text  text--lighten-2 caption font-weight-bold px-1",
            // style: "border-radius:8px; line-height:1.3;min-width:50px;"
        }
    }),
    warning: content => ({
        content,
        decoration:{
            class:"error--text  text--lighten-2 caption font-weight-bold px-1",
            // style: "border-radius:8px; line-height:1.3;min-width:50px;"
        }
    }),
    success: content => ({
        content,
        decoration:{
            class:"success--text  text--lighten-2 caption font-weight-bold px-1",
            // style: "border-radius:8px; line-height:1.3;min-width:50px;"
        }
    })
    
}


