// 
// resordId required
// 

    if(!window["Heart Harvest 1 Labeling Form_query"]){
        // 
        // Show application error 
        //

        showError('Recording ID required. Close this window.')

    } else {


    // 
    // logged In required
    // 

        if( !user.isLoggedIn ){    
            this.$djvue.login()
        }




    // 
    // getScript functionality
    // 
        let scripts = this.api.selectWidgets("528gs1z8wdl").config.scripts
        let getScript = name => _.find(scripts, s => s.name == name).script

    //
    // build initial config
    //
        this.$localStorage.set("harvest1_config", {
            db: {
                url: "mongodb://localhost:27017",
                name: "sparrow",
                labelingCollection: "harvest1",
                metadataCollection: "harvest1-metadata",
                historyCollection: "harvest1-history",
                grantCollection: "app-grant"
            },
            recordId: window["Heart Harvest 1 Labeling Form_query"].recordId
        })




    // 
    // Load data utilities 
    // 
        const loadRecord = async () => {
            let response = await this.api.runDps(getScript("GET_RECORD"), {options: window.app.config})
            let record = response.data
            record.nextTodo = ""
            window.app.prevComment = record["Stage Comment"]
            record["Stage Comment"] = ""

            // console.log("RECORD REACTIVE", record)
            window.app.record = record
            // console.log("RECORD", response.data)
        }

        const loadMetadata = async () => {
            let response  = await this.api.runDps(getScript("GET_METADATA"), {options: window.app.config})
            let metadata = {} 
            // console.log("METADATA", response.data)
            response.data.forEach( item => {
                metadata[item.name] = item
            })
            window.app.metadata = metadata
        }

        const loadForms = async patientId => {
            let response = await this.api.runDps(getScript("GET_FORMS"), {options: _.extend(window.app.config, {patientId})})
            window.app.form = response.data
            console.log("LOAD FORMS")
            this.emit("update-all-forms")
        }

        const loadGrants = async patientId => {
            let response = await this.api.runDps(getScript("GET_GRANTS"), {options: _.extend(window.app.config, {patientId})})
            window.app.grants = response.data
        }


        const loadUserList = async () => {
            let response = await axios.get("./api/users/list")
            window.app.users = response.data//.filter( u => u.altname)
        }

        window.app.submitForm = async record => {
            let response = await this.api.runDps(getScript("UPDATE_RECORD"), {options: _.extend(window.app.config, {record})})
            window.app.grants = response.data
        }

    // 
    //  normalize user role
    // 

        const normalizeRole = () => {
            console.log("NORM ROLE", user)
            let worker = _.find( window.app.grants, d => d.email.includes(user.email))

                if( worker ){
                    user.altname = worker.namedAs
                    user.role= worker.role
                } else {
                    user.altname = user.name
                    user.role = "Reader"
                }

                if(["dev", "admin", "CMO", "reader"].includes(user.role)) return

                if([window.app.record["1st expert"], window.app.record["2nd expert"]].includes(user.altname)) return

                user.role = "Reader"
        }

        const getTodoList = () => {

            if(["dev","admin", "CMO"].includes(user.role)) return [
                   "Continue Labeling",
                   "Complete 1st Stage",
                   "Reject 1st Stage",
                   "Complete 2nd Stage",
                   "Reject 2nd Stage",
                   "Complete Labeling"
                ]

            if(user.role == "1st expert") return [
                   "Continue Labeling",
                   "Complete 1st Stage"
                ]        

            if(user.role == "2nd expert") return [
                   "Continue Labeling",
                   "Complete 1st Stage",
                   "Reject 1st Stage",
                   "Complete 2nd Stage"
                ]

            return []    
        }

    const startApplication = async () => {
        window.app.config = this.$localStorage.get("harvest1_config")
            console.log ("START WITH CONFIG", window.app.config)

            // 
            // Load data
            // 

                await loadRecord()
                if(!window.app.record){
                    showError(`Labeling <strong>${window.app.config.recordId}</strong> not found. Close this.window`)
                }
                await loadForms(window.app.record["Examination ID"])
                await loadMetadata()
                await loadUserList()
                await loadGrants()

            //
            // Calculate user role
            //

                normalizeRole()
                window.app.todoList = getTodoList()
                window.app.todo = ""

            //
            // Activate  dashboard
            //

                this.emit("start")
                this.emit("expand-all",{ignore:["25klmtcysbm"]})

    }


    startApplication()
}

